function createEndpoint(accessToken, networkId, epName, appWan){
	gs.info('Creating Endpoint: ' + epName + ' with access to: ' + appWan);
	var endpointUrl = "https://gateway.production.netfoundry.io/core/v2/endpoints";
	var requestBody = '{"selected":false,"enrollmentMethod":{"ott":"true","ottca":null},"networkId":"'+networkId+'","attributes":["#'+appWan+'"],"name":"'+epName+'"}';
	gs.info("requestBody: " + requestBody);
	var responseBody;
	var response;
	var status;
	var sm;
	var endpointId;

	try {
		sm = new sn_ws.RESTMessageV2();
		sm.setEndpoint(endpointUrl);
		sm.setRequestHeader("Authorization", "Bearer " + accessToken);
		sm.setRequestHeader("content-type", "application/json");
		sm.setHttpMethod("POST");
		sm.setRequestBody(requestBody);
		response = sm.execute();
		responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
		status = response.getStatusCode();
		gs.info("status: " + status);
		if (status === 200) {
			var obj = JSON.parse(responseBody);
			gs.info("created endpoint: " + obj.name);
			endpointId = obj.id;
		}
	} catch(ex) {
		responseBody = ex.getMessage();
		status = '500';
		gs.error("exception: " + responseBody);
	} finally {
		requestBody = sm ? sm.getRequestBody():null;
	}
	return endpointId;
}
