sys_script_include.script:
function getToken() {
	var provider = new sn_cc.StandardCredentialsProvider();
	var credentials = provider.getCredentialByID("7ad31d762f912010455bae6df699b6b9");
	gs.info("User name: " + credentials.getAttribute("user_name"));
// 	gs.info("pw: " + credentials.getAttribute("password"));

	var clientId = credentials.getAttribute("user_name");
	var password = credentials.getAttribute("password");
	var authenticationUrl = "https://netfoundry-production-xfjiye.auth.us-east-1.amazoncognito.com/oauth2/token";

	var requestBody;
	var responseBody;
	var status;
	var sm;
	var acccessToken;
	try {
		sm = new sn_ws.RESTMessageV2();
		sm.setEndpoint(authenticationUrl);
		sm.setHttpMethod("POST");
		sm.setRequestHeader("content-type","application/x-www-form-urlencoded");
		sm.setRequestBody("grant_type=client_credentials&scope=https%3A%2F%2Fgateway.production.netfoundry.io%2F%2Fignore-scope");
		sm.setBasicAuth(clientId, password);
		response = sm.execute();
		responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
		status = response.getStatusCode();
		if (status === 200) {
			var obj = JSON.parse(responseBody);
			accessToken = obj.access_token;
		}
	} catch(ex) {
		responseBody = ex.getMessage();
		gs.error(responseBody);
	} finally {
		requestBody = sm ? sm.getRequestBody():null;
	}
	return accessToken;
}
