sys_script_include.script:
function shareEndpointViaEmail(accessToken, epName, epId, email){
	gs.info('Sharing Endpoint: ' + epName + ' via e-mail to: ' + email);
	var shareUrl = "https://gateway.production.netfoundry.io/core/v2/endpoints/share";	
	var requestBody = '[{"toList":["'+email+'"],"subject":"NetFoundry - Registration Information","id":"'+epId+'"}]' ;
	gs.info("requestBody: " + requestBody);
	var responseBody;
	var response;
	var status;
	var sm;

	try {
		sm = new sn_ws.RESTMessageV2();
		sm.setEndpoint(shareUrl);
		sm.setRequestHeader("Authorization", "Bearer " + accessToken);
		sm.setRequestHeader("content-type", "application/json");
		sm.setHttpMethod("POST");
		sm.setRequestBody(requestBody);
		response = sm.execute();
		responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
		status = response.getStatusCode();
		gs.info("status: " + status);
		if (status === 202) {
			var obj = JSON.parse(responseBody);
			gs.info("share endpoint: " + epName + " result: " + responseBody);
		}
	} catch(ex) {
		responseBody = ex.getMessage();
		status = '500';
		gs.info("exception: " + responseBody);
	} finally {
		requestBody = sm ? sm.getRequestBody():null;
	}
}
